<?php  require_once '../../init.inc.php';  require_once '../Inventory.class.php';  require_once '../Logger.class.php';  	// read $_GET  $rowID = intVal(@$_GET['row_id']);  $qty = intVal(@$_GET['qty']);	$price = floatVal(str_replace(",", ".", @$_GET['price']));  // sell  $inventory = new Inventory($_SESSION['playerID']);  $out = $inventory -> sellItem($rowID, $qty, $price);		// return back to template	echo json_encode(array(		'response' => $locale->translate($out), 		'result' => $out, 	));		exit();