<?php  require_once '../../init.inc.php';  require_once '../Inventory.class.php';  require_once '../Logger.class.php';  	// read $_GET  $itemID = intVal(@$_GET['item_id']);  $itemSID = intVal(@$_GET['item_subid']);  $qty = intVal(@$_GET['qty']);	$price = floatVal(str_replace(",", ".", @$_GET['price']));  // sell  $inventory = new Inventory($_SESSION['playerID']);  $out = $inventory -> sellItem($itemID, $itemSID, $qty, $price);		// return back to template	echo json_encode(array(		'response' => $locale->translate($out), 		'result' => var_export($inventory->status, true), 	));		exit();