<?php/** * Get player money - data can be store in another plugin *  * @author: Sekiphp */class Money{	private $settings;		/**	 * Set settings from config.inc.php	 */	public function __construct($settings){		$this->settings = $settings;				}  	/**	 * Get player Money	 */  public function getMoney($playerUUID){    $sql = "      SELECT " . $this->settings['money'] . "       FROM " . $this->settings['table'] . "      WHERE " . $this->settings['uuid'] . " = :player_uuid    ";    $where = array(      ":player_uuid" => $playerUUID,    );        $ret = DB2::assoc(DB2::query($sql, $where));    return $ret[$this->settings['money']];  }	/**	 * @param int $to Player UUID	 * @param double $money +/- amount	 */		public function newTransaction($to, $money){		$sql = "			UPDATE " . $this->settings['table'] . "  			SET " . $this->settings['money'] . " = (" . $this->settings['money'] . " + " . $money . ") 			WHERE " . $this->settings['uuid'] . " = ?		";    $params = array($to);        DB2::assoc(DB2::query($sql, $params));	}}